document.addEventListener("DOMContentLoaded",()=>{const e=document.documentElement,t=document.getElementById("themeToggleBtn");localStorage.getItem("color-theme")==="dark"?e.classList.add("dark"):e.classList.remove("dark"),t==null||t.addEventListener("click",()=>{e.classList.toggle("dark"),e.classList.contains("dark")?localStorage.setItem("color-theme","dark"):localStorage.setItem("color-theme","light")})});
